contract_address = '0x875c975E8e2aFa863855f79c85a6054a48596Af7'

web3 = Web3(Web3.HTTPProvider(bsc_testnet))
web3.middleware_onion.inject(geth_poa_middleware, layer=0)  #  Inject poa middleware

print("----------------------------------------------------")    
contract_instance = web3.eth.contract(address = contract_address,abi = abi)
token_supply = contract_instance.functions.totalSupply().call()
token_symbol = contract_instance.functions.symbol().call()
balanceOf = contract_instance.functions.balanceOf(account_address).call()
print("Token supply : ",token_supply)
print("Token symbol : ",token_symbol)
print("Token balanceOf : ",web3.toWei(balanceOf,'ether'))

nonce = web3.eth.getTransactionCount(account_address)
print("Nonce : ",nonce)
transaction = contract_instance.functions.transfer(account_address2,web3.toWei(100,'ether')).buildTransaction({
    'nonce': nonce,
    'gas': 100000,
    'gasPrice': web3.eth.gas_price
})
sign_transaction = web3.eth.account.sign_transaction(transaction,account_private_key)
transaction_hash = web3.eth.sendRawTransaction(sign_transaction.rawTransaction)

print("ERC20 Transfer success with hash ",web3.toHex(transaction_hash))